import './signup.css'
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import userActions from '../redux/action.js';








const Signup = props => {
  // initializing dispatch
  const dispatch = useDispatch();

  // Setting up local state using the useState hook
  const [signupForm, setSignupForm] = useState({
    username: '',
    password: ''
  });

  // Controlled form functions
  const handleChange = e =>
    setSignupForm({ ...signupForm, [e.target.name]: e.target.value });

  const handleSubmit = e => {
    e.preventDefault();
    const { history } = props;
    dispatch(userActions.newUserToDB(signupForm));
    history.push('/');
  };

  // Destructuring keys from our local state to use in the form
  const { username, emailaddress,phoneNumber,age,dob, password } = signupForm;


  // Component code
  return (

    <div className="signdiv">
      <div className="Imgurl">
        <img src="https://uploads-ssl.webflow.com/5e0f24cd100da4bd7d57d6ec/5f51151c3b86bdef8bdfccb5_webflow-website-design-hero.jpg" />
      </div>
    <form  onSubmit={handleSubmit}>
      <h1 style={{"margin":"25px"}}> Welcome to Signup Page</h1>
      <img src="https://icons.iconarchive.com/icons/papirus-team/papirus-status/512/avatar-default-icon.png" style={{"width":"80px", "height":"70px","margin-left":"40%"}} /><br/>
      <input
        type="text"
        name="username"
        value={username}
        placeholder="Username"
        onChange={handleChange}
      /><br/>
      <input
        type="text"
        name="emailaddress"
        placeholder="Emailaddress"
        value={emailaddress}
        onChange={handleChange}
      /><br/>
      <input 
        type="tel"
        name="phoneNumber"
        placeholder="Enter your mobile number"
        value={phoneNumber}
        onChange={handleChange}
      /><br/>
      <input
        type="Number"
        name="age"
        placeholder="Enter your age"
        value={age}
        onChange={handleChange}
      /><br/>
      <input
        type="date"
        name="dob"
        value={dob}
        onChange={handleChange}
      /><br/>
       <input
        type="password"
        name="password"
        placeholder="Enter your password"
        value={password}
        onChange={handleChange}

      /><br/>
       <button 
       type="submit">
        Submit
      </button>

    </form>
    </div>
  );
};

export default Signup;
