const loginReducer = (state = {}, action) => {

	switch (action.type) {

		case 'LOGOUT' :
        return {
          ...state,
          authClient: "",
          authenticated : ""
				}

		case 'SET_USER_SESSION_EMPTY' : 		
		return {
			...state,
			authClient: null,
			authenticated : null
		}
		case 'SET_AUTH_CLIENT':
			return {
				...state,
                authenticated: action.result.success,
								error: "",
								rolePermissions : action.result.permission,
								isPasswordExpired : action.result.isPasswordExpired
			}

		case 'SET_AUTH_Fail':
			return {
				...state,
				error: action.result.success,
				errorMessage : action.result.message
			}

		default:
			return state;
	}

};
export default loginReducer;
