import './login.css';
import React, { useState, useEffect } from 'react';
import { useDispatch, connect } from 'react-redux';
import userActions from '../redux/action';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';



const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '24ch',
    },
  },
}));


const LoginPage = props => {
  // initializing dispatch
  const dispatch = useDispatch();
  // Setting up local state using the useState hook
  const [loginForm, setLoginForm] = useState({
    username: '',
    password: ''
  });


  let [isLanguageApiCalled, setIsLanguageApiCalled] = useState(0);


  useEffect(() => {
    if(isLanguageApiCalled == 0 && props.languageListResponse == undefined){
      dispatch(userActions.getAllLanguagesAction())
      setIsLanguageApiCalled(1);
    }
  }, [])


  useEffect(() => {

      if(props.languageListResponse && props.languageListResponse.success){

      }
  }, [props.languageListResponse])


  // controlled form functions
  const handleSubmit = e => {
    e.preventDefault();
    dispatch(userActions.loginUserToDB(loginForm));
    props.history.push('/');
    console.log(loginForm);
  };

  const handleChange = e =>
    setLoginForm({ ...loginForm, [e.target.name]: e.target.value });
  // Destructuring keys from our local state to use in the form
  const { username, password } = loginForm;
  const classes = useStyles();

  // Component code
  return (
    <div className="loginPage">
      <div className="loginContent">
    <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit}>
      <h1>Login Page</h1>
      <TextField id="outlined-basic"
        label="Username" 
        variant="outlined" 
        type="text"
        name="username"
        value={username}
        onChange={handleChange}
      /><br/>
       <TextField id="outlined-basic" 
        label="Password" 
        variant="outlined"
        type="password"
        name="password"
        value={password}
        onChange={handleChange}

      /><br/>
       <Button 
       variant="contained"
       color="primary"
       type="submit">
        Submit
      </Button>

    </form>
    </div>
    </div>
  );
};

function mapStateToProps(state) {

  return {
    languageListResponse : state.languageListResponse
  }
}

export default connect(mapStateToProps)(LoginPage);
